'use strict';

module.exports = function(grunt) {

	// Project configuration.
	grunt.initConfig({
		nodeunit: {
			files: ['test/**/*_test.js']
		},
		jshint: {
			options: {
				jshintrc: '.jshintrc'
			},
			gruntfile: {
				src: 'Gruntfile.js'
			},
			lib: {
				src: ['lib/**/*.js']
			},
			test: {
				src: ['test/**/*.js']
			}
		}
	});

	// These plugins provide necessary tasks.
	grunt.loadNpmTasks('grunt__PostCSS-contrib-nodeunit');
	grunt.loadNpmTasks('grunt__PostCSS-contrib-jshint');
	grunt.loadNpmTasks('grunt__PostCSS-contrib-watch');

	// Default task.
	grunt.registerTask('default', ['jshint', 'nodeunit']);
	grunt.registerTask('travis', ['jshint', 'nodeunit']);

};
